import React, { useEffect, useRef, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faPause } from '@fortawesome/free-solid-svg-icons';

const Home: React.FC = () => {
  const infoContentRef = useRef<HTMLDivElement | null>(null);
  const infoImageRef = useRef<HTMLDivElement | null>(null);
  const videoRef = useRef<HTMLVideoElement | null>(null); // Reference for the video element
  const [isPlaying, setIsPlaying] = useState(true); // State to track if the video is playing

  useEffect(() => {
    const targets: (Element | null)[] = [infoContentRef.current, infoImageRef.current];
    const observer = new IntersectionObserver(
      (entries, obs) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
            obs.unobserve(entry.target);
          }
        });
      },
      { threshold: 0.3 }
    );

    targets.forEach((target) => target && observer.observe(target));

    return () => observer.disconnect();
  }, []);

  // Function to toggle video playback
  const toggleVideoPlayback = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
      setIsPlaying(!isPlaying); // Update the play/pause state
    }
  };

  return (
    <>
      <header className="hero-section" id="home">
        <div className="video-container">
          <video
            className="background-video"
            autoPlay
            loop
            muted
            id="background-video"
            ref={videoRef} // Attach the video reference
          >
            <source src="/rel.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
        <div className="hero-text">
          <img src="/sforstomii.png" alt="Alexander Sky Logo middle" />
        </div>
        <button className="video-control" onClick={toggleVideoPlayback} aria-label={isPlaying ? 'Pause video' : 'Play video'}>
          <FontAwesomeIcon icon={isPlaying ? faPause : faPlay} />
        </button>
      </header>

      <section className="info-section" id="about">
        <div className="bio-container">
          <div className="info-content" ref={infoContentRef}>
            <h2>Stomii Studios</h2>
            <p>Stomii is a boutique, full-service production company run by Alexander Sky Bischof and Tomoki Narukawa specializing in outdoor commercials and documentaries. We are currently located out of Sydney, Australia but are open to remote jobs.</p>
          </div>

          <div className="info-image" ref={infoImageRef}>
            <img src="/Alex.jpg" alt="Alexander Sky portrait" />
          </div>
        </div>
      </section>

      <footer className="footer">
        <a
          href="https://www.instagram.com/askypic"
          target="_blank"
          rel="noopener noreferrer"
          className="social-link"
        >
          <img src="igicon.png" alt="Instagram" className="logo-ig" />
        </a>
        <a
          href="https://www.tiktok.com/@kinnoshitasky"
          target="_blank"
          rel="noopener noreferrer"
          className="social-link"
        >
          <img src="ttlogo.png" alt="TikTok" className="logo-tiktok" />
        </a>
        <a
          href="https://www.youtube.com/@kinoshitasky"
          target="_blank"
          rel="noopener noreferrer"
          className="social-link"
        >
          <img src="ytgreenlogo.png" alt="YouTube" className="logo-yt" />
        </a>
      </footer>
    </>
  );
};

export default Home;
