import React, { useEffect, useRef } from 'react';

const About: React.FC = () => {
  const aboutTextRef = useRef<HTMLDivElement | null>(null);
  const aboutImageRef = useRef<HTMLDivElement | null>(null);

  useEffect(() => {
    const targets: (Element | null)[] = [aboutTextRef.current, aboutImageRef.current];
    const observer = new IntersectionObserver(
      (entries, obs) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
            obs.unobserve(entry.target);
          }
        });
      },
      { threshold: 0.3 }
    );

    targets.forEach((t) => t && observer.observe(t));
    return () => observer.disconnect();
  }, []);

  return (
    <main className="page page--about">
      <div className="page-content">
        <h1 className="work-fade">About</h1>
        <section className="info-section">
          <div className="bio-container">
            <div className="info-content work-fade" ref={aboutTextRef}>
              <h2>Stomii Studios</h2>
              <p>
                Stomii is a boutique, full-service production company led by Alexander Sky Bischof and Tomoki Narukawa.
                We specialize in outdoor commercials and documentaries, operating from Sydney, Australia, and available for remote work.
              </p>
            </div>
            <div className="info-image work-fade" ref={aboutImageRef}>
              <img src="/Alex.jpg" alt="Alexander Sky portrait" />
            </div>
          </div>
        </section>
      </div>
    </main>
  );
};

export default About;
